#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>
#include <stdbool.h>

int positionToNumber(int position[2], int keypad[3][3]) {
  int x = position[0];
  int y = position[1];
  return keypad[y][x];
}

void move(int position[2], char direction, int keypad[3][3]) {
  int x = position[0];
  int y = position[1];
  if (direction == 'L') {
    position[0] = (int)fmax(x - 1, 0);
  } else if (direction == 'R') {
    position[0] = (int)fmin(x + 1, 2);
  } else if (direction == 'U') {
    position[1] = (int)fmax(y - 1, 0);
  } else if (direction == 'D') {
    position[1] = (int)fmin(y + 1, 2);
  }
}

int main() {
  printf("Running Day 2 exercise\n");

  char PROBLEM_INPUT[] = "\\\\";

  int KEYPAD[3][3] = {{1, 2, 3}, {4, 5, 6}, {7, 8, 9}};

  size_t maxSize = 10;
  char * split[maxSize];
  char splitString[] = "\n";

  // split up input by newlines
  for (int i = 0; i < maxSize; i++) {
    if (i == 0) {
      split[i] = strtok(PROBLEM_INPUT, splitString);
    } else {
      split[i] = strtok(NULL, splitString);
      if (split[i] == NULL)
        break;
    }
  }

  // Go through each line following instructions
  int POSITION[2] = {1, 1};
  for (int i = 0; i < maxSize; i++) {
    char * stepStr = split[i];
    if (stepStr == NULL)
      break;
    int stepLen = strlen(stepStr);
    for (int j = 0; j < stepLen; j++) {
      move(POSITION, stepStr[j], KEYPAD);
    }
    printf("%d\n", positionToNumber(POSITION, KEYPAD));
  }

  return 0;
}